INSERT INTO `jindong_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('5', '2', '公司简介', '<p class=\"MsoNormal\" align=\"left\" style=\"text-indent: 21pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\"><b><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">许昌中州皇冠贸易有限公司隶属于河南省政府接待办中州国际集团，</span></b><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">是许昌市区唯一一家贵州茅台酒股份有限公司授权的国酒茅台专卖店，</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">目前在许昌经营高端白酒十年有余<span lang=\"EN-US\">,</span>企业规模一直在不断的发展壮大，在许昌高端白酒销售市场拥有着良好的信誉，并形成了一定的渠道网络资源，已拥有大量固定的客户群体和完善的销售网络体系，业务范围覆盖许昌市区及周边县区市场。</span><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent: 24pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\"><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">公司自<span lang=\"EN-US\">2001</span>年<span lang=\"EN-US\">8</span>月成立以来，依托集团公司雄厚经济实力和品牌优势，坚持恪守诚信经营理念，实现了跨越式发展，<span lang=\"EN-US\">2006</span>年<span lang=\"EN-US\">12</span>月公司转为一般纳税人。</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">公司自<span lang=\"EN-US\">2007</span>年成为茅台迎宾酒许昌地区总经销，通过几年来的努力，已在许昌地区建立了完善的茅台迎宾酒消费平台，并拥有稳定的客户资源及广泛的人脉关系，<span lang=\"EN-US\">2009</span>年<span lang=\"EN-US\">&mdash;2011</span>年连续三年取得全省销量第一名的佳绩。<span lang=\"EN-US\">2011</span>年<span lang=\"EN-US\">8</span>月，</span><span style=\"font-size:12.0pt;mso-bidi-font-size:10.0pt;line-height:150%;font-family:\r\n宋体;mso-ascii-font-family:&quot;Times New Roman&quot;;mso-bidi-font-family:宋体\">公司与洛阳杜康控股有限公司达成共识，成为战略合作伙伴，在许昌市代理酒祖杜康系列酒；</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">公司在销售团队的不懈努力下，<span lang=\"EN-US\">2011</span>年公司总销售额超过<span lang=\"EN-US\">1600</span>万元。公司良好的市场信誉和成熟的运作技巧使得公司在激烈的市场竞争中脱颖而出，成为豫南地区规范化发展的高档白酒经营企业；<span lang=\"EN-US\">2012</span>年<span lang=\"EN-US\">5</span>月，公司与贵州茅台酒股份有限公司签订&ldquo;仁酒&rdquo;经销协议，正式成为&ldquo;仁酒&rdquo;在许昌地区的唯一经销商。</span><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\"><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">凭借长期以来良好的企业形象，</span><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">2011</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">年我公司被评为许昌市区酒类销售行业唯一一家</span><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\">&ldquo;</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">百城万店无假货示范店</span><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\">&rdquo;</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">，</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">赢得了</span><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\">&ldquo;</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">买好酒，到中州</span><span lang=\"EN-US\" style=\"font-size: 12pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\">&rdquo;</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">的良好商誉。为了</span><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">让消费者<span lang=\"EN-US\">&ldquo;</span>来的开心、买的舒心、喝的放心<span lang=\"EN-US\">&rdquo;</span>，公司坚持走宁缺毋滥的精品经营路线，努力打造好许昌市民<span lang=\"EN-US\">&ldquo;</span>放心的酒坛子<span lang=\"EN-US\">&rdquo;</span>工程，为许昌消费者提供更加方便、快捷、保真、价优的优质服务，确保每一位消费者乘兴而来，满意而归。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\"><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">我公司愿意接受广大消费者的监督，并郑重承诺&ldquo;假一赔万元&rdquo;。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt; line-height: 150%; background-position: initial initial; background-repeat: initial initial;\"><span style=\"font-size: 12pt; line-height: 150%; font-family: 宋体; background-position: initial initial; background-repeat: initial initial;\">皇冠贸易 诚信至尊<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n<p>&nbsp;</p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">&nbsp;</span></p>', '', '', '', '0', '1', '1247818970', '', '0', 'http://', '');
INSERT INTO `jindong_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('6', '-1', '用户协议', '', '', '', '', '0', '1', '1247818970', '', '0', '', '');
INSERT INTO `jindong_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('7', '4', '三星电子宣布将在中国发布15款3G手机', '<p>全球领先的电子产品及第二大移动通信终端制造商三星电子今天在北京宣布，为全面支持中国开展3G移动通信业务，将在3G服务正式商用之际，向中国市场推出 15款3G手机。这些产品分别支持中国三大无线运营商的各种3G服务，并已经得到运营商的合作认可。凭借在电子和通信领域的整体技术实力和对消费者的准确 把握，三星电子已经开始全面发力中国的3G移动通信市场。<br />\n<br />\n&nbsp;&nbsp;&nbsp;&nbsp;2009年1月，中国政府宣布正式启动3G移动通信服务。并根据中国的实际情况，决定由三大运营商分别采用全部三种3G网络制式，&nbsp;即以中国自主知识产权为核心的TD-SCDMA，以欧洲为主要市场的WCDMA和源自北美的CDMA2000技术。<br />\n<br />\n&nbsp;&nbsp;&nbsp;&nbsp;多 年来，三星电子秉承&ldquo;做中国人民喜爱的企业，贡献于中国社会的企业&rdquo;的企业理念，准确地把握了中国社会的发展和市场的需求，推出了一系列深受中国消费者喜 爱的电子产品。为了配合中国推进具有自主知识产权的3G移动通信标准TD-SCDMA，&nbsp;三星电子从2000年开始在中国设立了通信技术研究院，&nbsp;开始进 行TD-SCDMA技术的研究。作为最早承诺支持中国3G标准的手机制造企业，三星电子已经先后投入了上亿元的研究费用，&nbsp;组建了几百人的研发团队。推出 的TD-SCDMA制式的产品，不仅通过了各级权威部门的试验和检测，也经历了2008年北京奥运会的现场考验。此次为中国移动定制的TD-SCDMA手 机，包括了满足高端商务需求的双待产品B7702C，以及数字电视手机、多媒体手机和时尚手机。<br />\n<br />\n&nbsp;&nbsp;&nbsp;&nbsp;作为世界第二大手机制造企业，三 星电子已经在全球3G市场积累了多年的技术和市场经验。最新的统计表明，在完全采用WCDMA标准的欧洲，三星电子的市场份额已经排名第二。在为中国联通 准备的产品中，不仅包括能够支持HSDPA的高端多媒体手机S7520U，也涵盖了能够支持高速上网、在线电影、在线音乐等适合不同消费需求的各种产品。<br />\n<br />\n&nbsp;&nbsp;&nbsp;&nbsp;而 在主要采用CDMA2000技术的北美市场，三星电子也取得了市场份额的第一名。即将陆续上市的支持中国电信3G服务的EVDO产品，有高端商务手机 W709。该机能够支持EVDO/GSM的双网双待功能，含800万像素拍摄系统。其他产品还包括音乐手机M609，双模手机W239，以及时尚设计的 F539等。<br />\n<br />\n&nbsp;&nbsp;&nbsp;&nbsp;作为世界上少数能够提供支持三种3G标准的终端厂商，三星电子正利用其在通信、半导体、显示器以及数字多媒体等方面 的优势，加快产品数字融合的进程。除上述3G手机产品外，三星电子也于近期推出了用于3G网络的上网卡和网络笔记本电脑。三星电子专务、无线事业部大中华 区高级副总裁卢基学先生说，&ldquo;上述15款新品，&nbsp;只是我们二季度新产品的一部分。随着中国3G移动通信市场的不断扩大，三星还将推出更多适合中国市场的终 端产品，以满足消费者对于通信和数字产品的不同需求。三星也将积极配合运营商业务的发展计划，加快技术和应用的研发。中国3G的移动通信市场前景将是非常 明亮的。&rdquo;</p>', '', '', '', '0', '1', '1241426864', '', '0', 'http://', '');
INSERT INTO `jindong_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('8', '4', '诺基亚牵手移动 5款热门TD手机机型推荐', '<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" align=\"center\" class=\"tableborder\">\r\n<tbody>\r\n<tr>\r\n<td width=\"180\" valign=\"top\" class=\"altbg4\">&nbsp;</td>\r\n<td height=\"100%\" valign=\"top\" class=\"altbg3\">\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" style=\"padding: 5px; table-layout: fixed; width: 973px; height: 2195px;\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\">\r\n<div class=\"bbs_content clearfix\">随着5月17日电信日的来临，自从09年初网民对于3G方面关注越来越多，目前国内3G网络主要有中国移动TD-SCDMA，中国联通WCDMA以及 中国电信的CDMA2000这三种制式。到底是哪一种网络制式能让消费者满意，相信好多消费者都难以判断。<br />\r\n<br />\r\n而作为3G网络最主要的组 成部分-手持终端（手机） ，相信也是好多消费者关注的焦点。目前，中国移动TD-SCDMA手机机型频频爆出，其中不乏亮点产品，像联想联想 Ophone、诺基亚、多普达 Magic等，下面就让笔者与大家一起来了解TD-SCDMA网络制式下的几款强势机型吧。<br />\r\n<br />\r\n诺基亚TD-SCDMA手机　型号：待定　参考报价：尚未上市<br />\r\n<br />\r\n随着国内3G网络发展速度加快及众多手机厂商纷纷跟进，诺基亚终于推出TD-SCDMA手机，这款诺基亚的首台TD-SCDMA测试手机型号目前仍无法 得知，但从键盘及菜单设计来看，我们可以是知道其并没有采用S60操作系统，只是配备了S40系统，机身直板造型与早期热卖的6300有几分相像。<br />\r\n<br />\r\n<p><img width=\"450\" alt=\"\" src=\"http://dstatic.esqimg.com/4812278/1.jpg\" style=\"display: block;\" /></p>\r\n<br />\r\n<br />\r\n图为：诺基亚TD-SCDMA手机<br />\r\n<br />\r\n虽然没有更多的参数资料，但是从曝光的图片我们可以知道这款诺基亚TD-SCDMA手机必定配备了QVGA分辨率的屏幕以及320万像素的摄像头，而标准的MP3以及蓝牙等功能自然不会缺少，在功能方面不会比以往的S40手机逊色。<br />\r\n<br />\r\n<p><img width=\"450\" alt=\"\" src=\"http://dstatic.esqimg.com/4812279/2.jpg\" style=\"display: block;\" /></p>\r\n<br />\r\n<br />\r\n图为：诺基亚TD-SCDMA手机<br />\r\n<br />\r\n这款诺基亚的TD手机最大的卖点便是提供了对TD-HSDPA技术的支持，最大的功能特色便是该技术被看为是TD网络下一步的演进技术，能够同时适用于 WCDMA和TD-SCDMA两种不同的网络支持，能够很好的支持非对称的数据业务，也就是说这款诺基亚手机的用户即便在全球漫游都能够使用到3G网络。 而其机身前置的摄像头也更是证实了其3G手机的身份。<br />\r\n<br />\r\n<p><img width=\"450\" alt=\"\" src=\"http://dstatic.esqimg.com/4812280/3.jpg\" style=\"display: block;\" /></p>\r\n<p><br />\r\n<br />\r\n图为：诺基亚TD-SCDMA手机<br />\r\n<br />\r\n从目前曝光的测试情况来看，通过这款诺基亚TD手机连接网络，其下载速度能够稳定在1.3Mbps左右，不过根据国内有些媒体的报道，诺基亚官方已经证实将于今天下半年配合运营商中国移动对出自己的第一款TD-SCDMA制式的S60手机，大家要拭目以待了。</p>\r\n<p>&nbsp;</p>\r\n图为：诺基亚TD-SCDMA手机<br />\r\n<br />\r\n最后较为耐人寻味的便是目前有业内人士指出目前曝光的的诺基亚TD手机其实是去年夏季就出现过的 TD测试手机，但是随着诺基亚拥有部分股份的TD芯片厂商&ldquo;凯明&rdquo;的倒闭，这款机型也就被取消了。尽管对于目前这款诺基亚的TD测试手机的身份尚无官方的 消息，但是诺基亚将推出TD手机遗失毫无悬念的事情了，相信大家也希望在TD制式下能够有更多的手机可以选择。</div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>', '', '', '', '0', '1', '1241427051', '', '0', 'http://', '');
INSERT INTO `jindong_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('85', '19', '帐户自助服务', '<p>&nbsp;<span style=\"font-family: Arial, Helvetica, sans-serif; line-height: 25px;\">在酒仙网首页&ldquo;登录/注册&rdquo;区成功注册或登录后，点击页面最上方&ldquo;我的帐户&rdquo;，我的帐户自助服务可实现以下多种功能：</span></p>\r\n<p style=\"padding: 0px; margin: 0px; color: rgb(62, 62, 62); font-family: Arial, Helvetica, sans-serif; line-height: 25px;\"><span style=\"padding: 0px; margin: 0px; color: black;\">1</span><span style=\"padding: 0px; margin: 0px; color: black;\"><span style=\"padding: 0px; margin: 0px;\">．<span style=\"padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\">&ldquo;</span></span><span style=\"padding: 0px; margin: 0px;\">账户管理&rdquo;分为：</span></span></span></p>\r\n<p style=\"padding: 0px; margin: 0px; color: rgb(62, 62, 62); font-family: Arial, Helvetica, sans-serif; line-height: 25px;\"><span style=\"padding: 0px; margin: 0px; color: black;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"padding: 0px; margin: 0px; color: black;\">我的订单：可查看、修改、确认订单。</span></p>\r\n<p style=\"padding: 0px; margin: 0px; color: rgb(62, 62, 62); font-family: Arial, Helvetica, sans-serif; line-height: 25px;\"><span style=\"padding: 0px; margin: 0px; color: black;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"padding: 0px; margin: 0px; color: black;\"><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\">&nbsp;</span></span><span style=\"padding: 0px; margin: 0px;\">我的礼券：</span>&nbsp;可查看用户所获的礼券名称、礼券编号、礼券面值、有效期限、使用状态。</span></p>\r\n<p style=\"padding: 0px; margin: 0px; color: rgb(62, 62, 62); font-family: Arial, Helvetica, sans-serif; line-height: 25px;\"><span style=\"padding: 0px; margin: 0px; color: black;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 评价管理：可对已购买的商品进行评论。</span></p>\r\n<p style=\"padding: 0px; mar