INSERT INTO `jindong_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('116', '22', '为什么喝绿茶对身体有好处', '<p class=\"cn\" style=\"text-align: center; padding-bottom: 10px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; letter-spacing: normal; padding-right: 0px; font: 14px/24px 微软雅黑; white-space: normal; orphans: 2; color: rgb(62,62,62); word-spacing: 0px; padding-top: 10px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><img align=\"absMiddle\" src=\"http://www.hecha.cn/themes/hechaV2.5/skin/tm5/images/goods/about_v4/logo.gif\" width=\"235\" height=\"49\" style=\"border-bottom-style: none; padding-bottom: 0px; margin: 0px; border-left-style: none; padding-left: 0px; padding-right: 0px; border-top-style: none; border-right-style: none; padding-top: 0px\" alt=\"\" /></p>\r\n<p style=\"text-align: right; padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 2em; margin: 0px; padding-left: 100px; letter-spacing: normal; padding-right: 100px; font: 14px/24px 微软雅黑; white-space: normal; orphans: 2; color: rgb(62,62,62); word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">&mdash;&mdash;让天下人都能喝上好茶！</p>\r\n<p style=\"text-align: left\"><br style=\"padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; letter-spacing: normal; padding-right: 0px; font: 12px/20px 微软雅黑; white-space: normal; orphans: 2; color: rgb(62,62,62); word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\" />\r\n&nbsp;</p>\r\n<p><br class=\"Apple-interchange-newline\" />\r\n<span style=\"widows: 2; text-transform: none; text-indent: 0px; letter-spacing: normal; display: inline !important; font: bold 18px/30px 微软雅黑; white-space: normal; orphans: 2; float: none; color: rgb(0,0,0); word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">绿茶的功效与作用</span></p>\r\n<div class=\"txnew\" style=\"padding-bottom: 0px; widows: 2; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; letter-spacing: 1px; padding-right: 0px; font: 14px/16px Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; color: rgb(51,51,51); word-spacing: 0px; text-decoration: ; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\">绿茶其保留了许多天然物质，科学证明<a href=\"http://www.hecha.cn/lvcha_list.html\" style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0,51,255); text-decoration: ; padding-top: 0px; -webkit-transition: color 0.2s ease-in\">绿茶</a>中含有的有机物多大四百五十多种，无机物有十五种。这些物质大部分都有保健功效其杀菌、抗癌效果是其他茶类所比不了的。下面主要说说绿茶三大功效。</p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\"><img alt=\"\" src=\"http://www.hecha.cn/images/upload/Image/u=2013840570,614559443&amp;fm=52&amp;gp=0(1).jpg\" width=\"182\" height=\"147\" style=\"border-bottom-style: none; padding-bottom: 0px; margin: 0px; border-left-style: none; padding-left: 0px; padding-right: 0px; border-top-style: none; border-right-style: none; padding-top: 0px\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\"><b style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">一、延缓衰老</b></p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\">总所周知绿茶最大的好处之一就是&mdash;&mdash;抵抗岁月的衰老深受广大爱美女性的青睐。医学研究证明人体每天新城代谢时如果过于氧化会产生大量的自由基(容易老化)，绿茶中含有大量的茶多酚，而茶多酚具有很强的抗氧化性，是自由基的清除剂。绿茶抵抗衰老的效果是维生素E的十倍。</p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\"><img alt=\"\" src=\"http://www.hecha.cn/images/upload/Image/u=745403819,2497423774&amp;fm=52&amp;gp=0(1).jpg\" width=\"184\" height=\"138\" style=\"border-bottom-style: none; padding-bottom: 0px; margin: 0px; border-left-style: none; padding-left: 0px; padding-right: 0px; border-top-style: none; border-right-style: none; padding-top: 0px\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\"><b style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">二、抗癌</b></p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\">现在医学证明茶多酚对胃癌、肠癌等多种癌症的预防和辅助治疗有很大的益处。绿茶中较多的茶多酚还可以阻止亚硝酸铵等多种致癌物质在体内合成，并直接杀死癌细胞具有提高免疫能力的功效。</p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\"><img alt=\"\" src=\"http://www.hecha.cn/images/upload/Image/u=3245604610,445573941&amp;fm=23&amp;gp=0.jpg\" width=\"187\" height=\"159\" d=\"\" style=\"border-bottom-style: none; padding-bottom: 0px; margin: 0px; border-left-style: none; padding-left: 0px; padding-right: 0px; border-top-style: none; border-right-style: none; padding-top: 0px\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\"><b style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">三、降血脂</b></p>\r\n<p style=\"text-align: left; padding-bottom: 10px; line-height: 24px; text-indent: 2em; margin: 0px; padding-left: 0px; padding-right: 0px; font-size: 14px; padding-top: 10px\">绿茶中还有其他较多的元素，如：儿茶素能够降低血浆中的总胆固醇油脂的量同时还能够增加蛋白质密度。既可以防止血液凝块及血小板成团降低心血管发生。</p>\r\n</div>\r\n<p>&nbsp;</p>', '', '', '', '0', '0', '1372636148', '', '0', 'http://', '');
