INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('344', '879', 'ECS000344', '50°宋河粮液专供酒500ml', '+', '8', '188', '', '10000', '0.000', '70.80', '59.00', '0.00', '0', '0', '1', '', '', '<h4 class=\"h4_ass\" sizset=\"36\" sizcache=\"39\">商品特点</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"39\" sizcache=\"17\">\r\n<p><font color=\"#666666\" size=\"2\">宋河粮液以优质高粱为原料,精选优质小麦等制曲,采用固态泥池发酵,传统工艺与现代科技相结合,精工酿制而成。宋河粮液具有无色透明,窖香浓郁,绵甜爽净,回味悠长的特点。宋河粮液专供酒香的庄重,甜得大方,绵的亲切,净得脱俗。</font></p>\r\n<p sizset=\"39\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/images/upload/Image/pd_tit_kinds_02.jpg\" width=\"750\" height=\"86\" style=\"border-bottom: medium none; border-left: medium none; padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; border-top: medium none; border-right: medium none; padding-top: 0px; border-image: initial\" /></p>\r\n</dl><!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"40\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/3913/g/1389668810343.jpg\" alt=\"\" /> </dl><!--detail_prtpic over--></div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">品牌文化</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"41\" sizcache=\"17\">\r\n    <tbody sizset=\"41\" sizcache=\"17\">\r\n        <tr sizset=\"41\" sizcache=\"17\">\r\n            <td sizset=\"41\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/5312/b/1351941013462.jpg\" alt=\"\" /></td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">宋河产自老子故里鹿邑，位居淮河名酒带源头，酿造历史可以追溯到春秋战国时期。孔子曾问礼老子于此，两位圣贤把盏论道，孔子不胜酒力，三日方醒，留下了&ldquo;为酒无量不及乱&rdquo;的处世箴言。作为鹿邑最富盛名的文化资源，老子的存在让历史上跨越千年的八位皇者亲临鹿邑，以地产酒拜谒老子，让这片土地上留下了绵绵不断的酒史传承。</font></p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">酒厂介绍</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"42\" sizcache=\"17\">\r\n    <tbody sizset=\"42\" sizcache=\"17\">\r\n        <tr sizset=\"42\" sizcache=\"17\">\r\n            <td sizset=\"42\" sizcache=\"17\">\r\n            <p sizset=\"42\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/5312/m/1351941019774.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">河南省宋河酒业股份有限公司属于国家大型一类企业，为17大中国名酒生产企业之一，公司所在地河南省鹿邑县枣集镇，是我国著名的传统酒乡，是道家鼻祖老子李尔的诞生地和道教文化的发祥地。宋河酿酒始于春秋盛于隋唐，现在已发展成规模宏大的酿酒工业园区。先后被评为河南轻工企业十强，河南工业企业50强和国家工业企业500强、国家重点企业，连续多年位居周口市场工业企业第一名。自建厂以来累计实现利税数十亿元，是河南利税大户和周口市重要财政支柱。 </font></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n        <tr sizset=\"43\" sizcache=\"17\">\r\n            <td sizset=\"43\" sizcache=\"17\">\r\n            <p sizset=\"43\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/5312/m/1351941022386.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">公司主要生产中国名酒、河南名牌&ldquo;宋河粮液&rdquo;和河南名牌&ldquo;鹿邑大曲&rdquo;及其系列产品。主导产品宋河粮液1979年被评为河南名优产品，1984年获轻工业部银杯奖，1988年在全国第五届白酒评比中，获国家金质奖和中国名酒称号。在1994年和2001年两次中国名酒复评中，以其稳定的质量、良好的口感，两次蝉联中国名酒称号。先后被授予全国用户满意产品、河南省名牌产品、河南省食品工业第一品牌、省重点保护产品、省免检产品、省著名商标、省知名商品，&ldquo;宋河&rdquo;商标被国家工商总局授予中国驰名商标等荣誉。主导产品宋河粮液、鹿邑大曲先后荣获河南省免检产品、河南省名牌产品、河南省知名商品等荣誉。企业先后通过ISO9001：2000国际质量管理体系认证、HACCP食品安全管理体系认证、白酒纯粮固态发酵标志认证、优级产品认证等第三方认证，在2007年1月13日在河南省首家通过白酒&ldquo;食品生产许可证现场核查&rdquo;（A级）。企业产值、利税连续几年稳居河南白酒行业第一名，在国内白酒业居于举足轻重的地位。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">获奖荣誉</h4>\r\n<p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">&gt;&gt;</font></strong> 1988年 荣获中国名酒称号及金质奖（是河南唯一获得中国名酒称号的浓香型白酒）；</font></p>\r\n<p><font color=\"#666666\" size=\"2\"><font color=\"#ff0000\"><strong><font color=\"#333333\">&gt;&gt;</font></strong> </font>2004年 宋河商标被国家工商总局认定为&ldquo;中国驰名商标&rdquo;；</font></p>\r\n<p><font color=\"#666666\" size=\"2\"><font color=\"#ff0000\"><strong><font color=\"#333333\">&gt;&gt;</font></strong> </font>2006年 宋河粮液获得&ldquo;白酒工业十大创新品牌&rdquo;及&ldquo;中华文化名酒&rdquo;称号；</font></p>\r\n<p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">&gt;&gt;</font></strong> 2010年 宋河粮液当选为&ldquo;上海世博会河南省馆唯一指定用酒&rdquo;；</font></p>\r\n<p><font size=\"2\"><font color=\"#666666\"><strong><font color=\"#333333\">&gt;&gt;</font></strong> 2011年 &ldquo;宋河&rdquo;被国家商务部认定为&ldquo;中华老字号&rdquo;，</font><font color=\"#666666\">被联合国环境规划基金会授予&ldquo;杰出绿色健康食品&rdquo;；</font></font></p>\r\n<p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">&gt;&gt;</font></strong> 2012年 宋河股份荣获&ldquo;中华慈善奖&rdquo;之&ldquo;最具爱心捐赠企业&rdquo;；</font></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"44\" sizcache=\"17\">\r\n    <tbody sizset=\"44\" sizcache=\"17\">\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/7803/h/1366344039446.jpg\" alt=\"\" /></td>\r\n            <td sizset=\"45\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/7245/h/1363944742881.jpg\" alt=\"\" /></td>\r\n            <td sizset=\"46\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/7245/h/1363944749366.jpg\" alt=\"\" /></td>\r\n            <td sizset=\"47\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/7245/h/1363944757891.jpg\" alt=\"\" /></td>\r\n            <td sizset=\"48\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/7245/h/1363944764802.jpg\" alt=\"\" /></td>\r\n            <td sizset=\"49\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/7245/h/1363945044149.jpg\" alt=\"\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p><!--part_box--><!--part_box--><!-- 酒具适用场合 start--><!-- 酒具适用场合 end--><!--part_box--><input id=\"sych\" type=\"hidden\" value=\"商务用酒;礼节拜访;聚会用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"41\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"41\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"56\" sizcache=\"27\">\r\n<div id=\"gvsw0\" class=\"sych1\" sizset=\"56\" sizcache=\"27\"><span sizset=\"50\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvsw1.gif\" alt=\"\" /> </span><span sizset=\"51\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvhq0.gif\" alt=\"\" /> </span><span sizset=\"52\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvlj1.gif\" alt=\"\" /> </span><span sizset=\"53\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvjh1.gif\" alt=\"\" /></span></div>\r\n</div>\r\