INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('359', '513', 'ECS000359', '39°汾酒北特加蓝方475ml', '+', '63', '171', '', '10000', '0.000', '66.00', '55.00', '55.00', '1384934400', '1577520000', '1', '', '酝酿真藏 无上妙品', '<p>商品特点</p>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"39\" sizcache=\"17\">\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"39\" sizcache=\"17\">\r\n    <tbody sizset=\"39\" sizcache=\"17\">\r\n        <tr sizset=\"39\" sizcache=\"17\">\r\n            <td sizset=\"39\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/upload/goods/101/f/1366188252699.jpg\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">北特加：</font></strong> &ldquo;北特加&rdquo;酒是汾酒集团最前卫的战略性品牌，它是传统发酵工艺与现代生物技术、纯正清香型与国际流行口感及中西方酒文化相融合的经典之作，是专为时尚、流行的现代人打造的，酒液具有入口绵、落口甜、饮后余香、回味悠长等特点。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<img alt=\"\" width=\"750\" height=\"86\" style=\"border-bottom: medium none; border-left: medium none; padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; border-top: medium none; border-right: medium none; padding-top: 0px\" src=\"http://img01.jiuxian.com/images/upload/Image/pd_tit_kinds_03.jpg\" /> </dl><!--part_box over--><!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">品牌文化</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"750\" sizset=\"41\" sizcache=\"17\">\r\n    <tbody sizset=\"41\" sizcache=\"17\">\r\n        <tr sizset=\"41\" sizcache=\"17\">\r\n            <td sizset=\"41\" sizcache=\"17\">\r\n            <p sizset=\"41\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/upload/goods/4867/b/1351677543908.jpg\" /></p>\r\n            </td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">从考古发掘文物证实，山西杏花村汾酒酿酒史可上溯到4000多年前的史前期的龙山文化时期。&ldquo;杏花村&rdquo;汾酒早已成为宫廷御酒，应该说：&ldquo;北齐宫廷酒，今日杏花村&rdquo;。这是杏花村汾酒可靠的第一次成名，汾酒史上的第一座丰碑，应申报世界文化遗产。北周文学家庚信亦有&ldquo;三春竹叶青，一曲鹍鸡弦&rdquo;的名句赞誉竹叶青酒。梁简文帝肖纲有：&ldquo;兰羞荐俎，竹酒澄芳&rdquo;的诗句称赞竹叶青酒。</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">唐代与清代更是&ldquo;杏花村&rdquo;汾酒酿酒史上两个鼎盛时期，到唐朝杏花村酿酒作坊达70多家，出现了&ldquo;长街恰副登瀛处，处处街头揭翠帘&rdquo;的盛况。杜甫也到此畅饮，李白前来&ldquo;醉校古碑&rdquo;写下：&ldquo;琼杯绮食青玉案，使我醉饱无归心&rdquo;的诗句。唐代以前的酒是&ldquo;浊酒&rdquo;，&ldquo;杏花村&rdquo;汾酒于唐代在北齐汾清酒的基础上酿出了中国酿酒史上第一家&ldquo;蒸馏白酒&rdquo;成为&ldquo;汾洲贡酒&rdquo;，今天应该说&ldquo;唐时宫廷酒，今日杏花村&rdquo;。到了清代，杏花村的汾酒作坊多达200多家。</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">汾酒发展至今依然是中国酒类行业中一颗璀璨的明星。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">酒厂介绍</h4>\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"50\" sizcache=\"27\">\r\n    <tbody sizset=\"50\" sizcache=\"27\">\r\n        <tr sizset=\"50\" sizcache=\"27\">\r\n            <td sizset=\"42\" sizcache=\"17\">\r\n            <p sizset=\"42\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/upload/goods/4867/m/1351677549332.jpg\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td sizset=\"50\" sizcache=\"27\">\r\n            <p sizset=\"50\" sizcache=\"27\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\"><span style=\"text-align: left\"><font color=\"#666666\" size=\"2\">山西杏花村汾酒集团有限责任公司为国有独资公司，以生产经营中国名酒&mdash;&mdash;汾酒、竹叶青酒为主营业务，年产名优白酒5万吨，是全国最大的名优白酒生产基地之一。</font></span></p>\r\n            <p style=\"text-align: left; padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\"><font color=\"#666666\" size=\"2\">集团公司下设22个子、分公司，员工8000人，占地面积230万平方米，建筑面积76万平方米。核心企业汾酒厂股份有限公司为公司最大全资子公司，1993年在上海证券交易所挂牌上市，为中国白酒第一股，山西第一股。</font></p>\r\n            </td>\r\n        </tr>\r\n        <tr sizset=\"43\" sizcache=\"17\">\r\n            <td sizset=\"43\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/upload/goods/4867/m/1351677554198.jpg\" /></td>\r\n            <td>\r\n            <p style=\"text-align: left; padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\"><font color=\"#666666\" size=\"2\">公司拥有&ldquo;杏花村&rdquo;、&ldquo;竹叶青&rdquo;两个中国驰名商标，据2006年《中国500最具价值品牌排行榜》公布，&ldquo;杏花村&rdquo;品牌价值已达47.76亿元。公司主导产品有汾酒、竹叶青酒、玫瑰汾酒、白玉汾酒、以及葡萄酒、啤酒等六大系列。汾酒是我国清香型白酒的典型代表，素以入口绵、落口甜、饮后余香、回味悠长而著称，在国内外享有较高的知名度、美誉度和忠诚度。主要品种有国藏汾酒、青花瓷汾酒、中华汾酒、老白汾酒等。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\">&nbsp;</p>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">获奖荣誉</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"750\" sizset=\"44\" sizcache=\"17\">\r\n    <tbody sizset=\"44\" sizcache=\"17\">\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\"><img alt=\"\" style=\"width: 347px; height: 254px\" src=\"http://img01.jiuxian.com/upload/goods/4867/h/1348543662502.jpg\" /></td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">1995年，山西&ldquo;一会一节&rdquo;综合展览工作先进单位；</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">中国工业企业综合评价最优500家前百强 ； </font></p>\r\n            <p><font color=\"#666666\" size=\"2\">省经贸委&ldquo;八五&rdquo;改造先进集体 ； </font></p>\r\n            <p><font color=\"#666666\" size=\"2\">1990-1995年全国职工教育先进单位；</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">省轻工总会&ldquo;八五&rdquo;教育先进单位 ；</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">1997 年，杏花村商标被 国家工商局评为全国驰名商标； </font></p>\r\n            <p><font color=\"#666666\" size=\"2\">走向世界中国名牌100强；</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">2004年，获中国食品工业百强企业；中国轻工业企业信息化先进单位。 </font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p><!--part_box--><!--part_box--><!-- 酒具适用场合 start--><!-- 酒具适用场合 end--><!--part_box--><input id=\"sych\" type=\"hidden\" value=\"聚会用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"51\" sizcache=\"27\">\r\n<div id=\"gvsw0\" class=\"sych1\" sizset=\"51\" sizcache=\"27\"><span sizset=\"45\" sizcache=\"17\"><img alt=\"\" src=\"http://misc.jiuxian.com/img/goods/gvsw0.gif\" /> </span><span sizset=\"46\" sizcache=\"17\"><img alt=\"\" src=\"http://misc.jiuxian.com/img/goods/gvhq0.gif\" /> </span><span sizset=\"47\" sizcache=\"17\"><img alt=\"\" src=\"http://misc.jiuxian.com/img/goods/gvlj0.gif\" /> </span><span sizset=\"48\" sizcache=\"17\"><img alt