INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('386', '638', 'ECS000386', '53°茅台仁酒500ml', '+', '5', '187', '', '10000', '0.000', '238.79', '199.00', '160.00', '1397635200', '1524988800', '1', '', '', '<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizcache=\"2131\" sizset=\"40\">\r\n<h4 class=\"h4_ass\" sizcache=\"38\" sizset=\"38\">商品特点</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizcache=\"2131\" sizset=\"40\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizcache=\"2131\" sizset=\"40\">\r\n    <tbody sizcache=\"2131\" sizset=\"40\">\r\n        <tr sizcache=\"2131\" sizset=\"40\">\r\n            <td sizcache=\"2131\" sizset=\"40\"><img src=\"http://img01.jiuxian.com/upload/goods/5031/f/1352379188511.jpg\" alt=\"\" /></td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">●  仁酒文化，经典酱香：</font></strong>仁酒是一款凝聚了茅台人&lsquo;仁者仁心&rsquo;的经典酱香型白酒。在中国的传统文化中，儒家文化一直居于正统地位，儒家文化的核心就是&ldquo;仁&rdquo;，而仁酒的诞生，就是为了呼应传统文化的回归，见证中华文明的复兴。</font><font color=\"#666666\" size=\"2\">仁酒的核心价值在于其品质和文化，从品质上讲，它秉承了酱香传统工艺，能够让消费者于绿色、健康的酱香品质中，感受到愉悦、舒适的身心体验；从文化上讲，它所蕴含的&ldquo;仁政、仁商、仁人&rdquo;的传统智慧，为消费者带来情感上的共鸣。 </font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</dl></div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizcache=\"2131\" sizset=\"41\">\r\n<h4 class=\"h4_ass\" sizcache=\"38\" sizset=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizcache=\"2131\" sizset=\"41\">\r\n<p align=\"center\" sizcache=\"2131\" sizset=\"41\"><img src=\"http://img01.jiuxian.com/upload/goods/5031/g/1352380123290.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizcache=\"2131\" sizset=\"42\"><img src=\"http://img01.jiuxian.com/upload/goods/5031/g/1352380128321.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizcache=\"2131\" sizset=\"43\"><img src=\"http://img01.jiuxian.com/upload/goods/5031/g/1352378605611.jpg\" alt=\"\" /></p>\r\n</dl><!--detail_prtpic over--></div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizcache=\"2131\" sizset=\"44\">\r\n<h4 class=\"h4_ass\" sizcache=\"38\" sizset=\"40\">品牌文化</h4>\r\n<p sizcache=\"2131\" sizset=\"44\">&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizcache=\"2131\" sizset=\"44\">\r\n    <tbody sizcache=\"2131\" sizset=\"44\">\r\n        <tr sizcache=\"2131\" sizset=\"44\">\r\n            <td sizcache=\"2131\" sizset=\"44\">\r\n            <p sizcache=\"2131\" sizset=\"44\"><img src=\"http://img01.jiuxian.com/upload/goods/13/b/1351505874983.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">茅台酒是世界三大著名蒸溜酒之一，誉称国酒，在国内外享有盛名。产于中国贵州茅台镇，以本地优质糯高梁、小麦、水为原料，利用得天独厚的自然环境，采用科学独特的传统工艺精心酿制而成，未添加任何香气、香味物质，从生产、贮存到同出厂历经五年以上。  </font></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n        <tr sizcache=\"2131\" sizset=\"45\">\r\n            <td sizcache=\"2131\" sizset=\"45\"><img src=\"http://img01.jiuxian.com/upload/goods/13/b/1351505933565.jpg\" alt=\"\" /></td>\r\n            <td><font color=\"#666666\" size=\"2\">茅台酒具有酱香突出、幽雅细腻、酒体醇厚、回味悠长、空杯留香持久的特点。茅台酒是风格最完美的酱香型大曲酒之典型，故&ldquo;酱香型&rdquo;又称&ldquo;茅香型&rdquo;。其酒质晶亮透明，微有黄色，酱香突出，令人陶醉，敞杯不饮，香气扑鼻，开怀畅饮，满口生香，饮后空杯，留香更大，持久不散。口味幽雅细腻，酒体丰满醇厚，回味悠长，茅香不绝。茅台酒液纯净透明、醇馥幽郁的特点，是由酱香、窖底香、醇甜三大特殊风味融合而成，现已知香气组成成分多达300余种。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizcache=\"2131\" sizset=\"46\">\r\n<h4 class=\"h4_ass\" sizcache=\"38\" sizset=\"41\">酒厂介绍</h4>\r\n<p sizcache=\"2131\" sizset=\"46\">&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizcache=\"2131\" sizset=\"46\">\r\n    <tbody sizcache=\"2131\" sizset=\"46\">\r\n        <tr sizcache=\"2131\" sizset=\"46\">\r\n            <td sizcache=\"2131\" sizset=\"46\"><img src=\"http://img01.jiuxian.com/upload/goods/3/m/1351220175346.jpg\" alt=\"\" /></td>\r\n            <td>\r\n            <p>&nbsp;</p>\r\n            <p><font color=\"#666666\" size=\"2\"><font color=\"#666666\">贵州茅台酒股份有限公司是由中国贵州茅台酒厂有限责任公司、贵州茅台酒厂技术开发公司、贵州省轻纺集体工业联社、深圳清华大学研究院、中国食品发酵工业研究所、北京糖业烟酒公司、江苏省糖烟酒总公司、上海捷强烟草糖酒（集团）有限公司等八家公司共同发起，并经过贵州省人民政府黔府函字（1999）291号文件批准设立的股份有限公司，注册资本为一亿八千五百万元。</font>  </font></p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p sizcache=\"2131\" sizset=\"47\">&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizcache=\"2131\" sizset=\"47\">\r\n    <tbody sizcache=\"2131\" sizset=\"47\">\r\n        <tr sizcache=\"2131\" sizset=\"47\">\r\n            <td sizcache=\"2131\" sizset=\"47\"><img src=\"http://img01.jiuxian.com/upload/goods/3/m/1351220181562.jpg\" alt=\"\" /></td>\r\n            <td>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            <font color=\"#666666\">\r\n            <p><font color=\"#666666\" size=\"2\">公司主营贵州茅台酒系列产品的生产和销售，同时进行饮料、食品、包装材料的生产和销售，防伪技术开发，信息产业相关产品的研制开发。</font></p>\r\n            </font>\r\n            <p><font color=\"#666666\" size=\"2\">目前，贵州茅台酒股份有限公司茅台酒年生产量已突破一万吨；43&deg;、38&deg;、33&deg;茅台酒拓展了茅台酒家族低度酒的发展空间；茅台王子酒、茅台迎宾酒满足了中低档消费者的需求；15年、30年、50年、80年陈年茅台酒填补了我国极品酒、年份酒、陈年老窖的空白；在国内独创年代梯级式的产品开发模式。形成了低度、高中低档、极品三大系列70多个规格品种，全方位跻身市场，从而占据了白酒市场制高点，称雄于中国极品酒市场。</font></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizcache=\"2131\" sizset=\"48\">\r\n<h4 class=\"h4_ass\" sizcache=\"38\" sizset=\"42\">获奖荣誉</h4>\r\n<font color=\"#666666\"><font size=\"2\">1915年，美国巴拿马万国博览会金奖；<br />\r\n1985年，国际美食旅游大赛金桂叶奖；<br />\r\n1986年，法国巴黎第十二届国际食品博览会金奖；<br />\r\n1986年，香港&quot;亚洲之星&quot;包装奖；<br />\r\n1987年，香港第三届世界广告大会、中国出口广告一等奖；<br />\r\n1989年，首届北京国际博览会金奖；<br />\r\n1991年，第二届北京国际博览会金奖；<br />\r\n1992年，首届美国国际名酒大赛金奖；<br />\r\n1992年，香港国际食品博览会最高金奖；<br />\r\n1992年，日本东京第四届国际名酒博览会金奖；<br />\r\n1992年，&ldquo;汉帝茅台酒&rdquo;包装获法国巴黎&quot;世界之星&quot;国际包装最高奖；<br />\r\n1993年，法国波尔多葡萄酒烈性酒展览会特别奖；<br />\r\n1994年，&quot;飞天牌&quot;、&quot;五星奖&quot;系列茅台酒获第五届亚太国际贸易博览会金奖；<br />\r\n1994年，（美国）纪念巴拿马万国博览会80周年，名酒品评会特别金奖第一名。</font><font size=\"3\"> </font></font>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizcache=\"2131\" sizset=\"48\">\r\n    <tbody sizcache=\"2131\" sizset=\"48\">\r\n        <tr sizcache=\"2131\" sizset=\"48\">\r\n            <td sizcache=\"2131\" sizset=\"48\"><img src=\"http://img01.jiuxian.com/upload/goods/2645/h/1350115398793.jpg\" alt=\"\" /></td>\r\n            <td sizcache=\"2131\" sizset=\"49\"><img src=\"http://img01.jiuxian.com/upload/goods/4798/h/1350113892346.jpg\" alt=\"\" /></td>\r\n            <td sizcache=\"2131\" sizset=\"50\"><img src=\"http://img01.jiuxian.com/upload/goods/4798/h/1350113959734.jpg\" width=\"120\" height=\"118\" alt=\"\" /></td>\r\n            <td sizcache=\"2131\" sizset=\"51\"><img src=\"http://img01.jiuxian.com/upload/goods/4798/h/1350113949577.jpg\" alt=\"\" /></td>\r\n            <td sizcache=\"2131\" sizset=\"52\"><img src=\"http://img01.jiuxian.com/upload/goods/4798/h/1350898641721.gif\" alt=\"\" /></td>\r\n            <td sizcache=\"2131\" sizset=\"53\"><img src=\"http://img01.jiuxian.com/upload/goods/4798/h/1350898622830.jpg\" width=\"120\" height=\"154\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box--><!--part_box--><!-- 酒具适用场合 start--><!-- 酒具适用场合 end--><!--part_box-->\r\n<p><input id=\"sych\" value=\"商务用酒;礼节拜访\" type=\"hidden\" /></p>\r\n<div class=\"part_box clearfix\" sizcache=\"2131\" sizset=\"54\">\r\n<h4 class=\"h4_ass\" sizcache=\"38\" sizset=\"43\">适宜场合</h4>\r\n<h4 class=\"h4_ass\" sizcache=\"38\" sizset=\"43\">\r\n<div class=\"sych\" sizcache=\"2131\" sizset=\"54\">\r\n<div id=\"gvsw0\" class=\"sych1\" sizcache=\"2131\" sizset=\"54\"><span sizcache=\"2131\" sizset=\"54\"><img src=\"http://misc.jiuxian.com/img/goods/gvsw1.gif\" alt=\"\" /> </span><span sizcache=\"2131\" sizset=\"55\"><img src=\"http://misc.jiuxian.com/img/goods/gvhq0.gif\" alt=\"\" /> </span><span sizcache=\"2131\" sizset=\"56\"><img src=\"http://misc.jiuxian.com/img/goods/gvlj1.gif\" alt=\"\" /> </span><span sizcache=\"2131\" sizset=\"57\"><img src=\"http://misc.jiuxian.com/img/goods/gvjh0.gif\" alt=\"\" /></span></div>\r\n</div>\r\n</h4>\r\n</div>', 'images/201404/thumb_img/386_thumb_G_1398034732069.jpg', 'images/201404/goods_img/386_G_1398034732547.jpg', 'images/201404/source_img/386_G_1398034732536.jpg', '1', '', '1', '1', '1', '1386785173', '100', '0', '0', '0', '0', '1', '0', '1398034940', '0', '', '-1', '-1', '0', '', '0');
