INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('427', '684', 'ECS000427', '52°五粮液万事如意珍藏（新品）500ml', '+', '0', '169', '', '1', '0.000', '636.00', '169.00', '0.00', '0', '0', '1', '', '', '<h4 class=\"h4_ass\" sizset=\"36\" sizcache=\"39\">商品特点</h4>\r\n<p>&nbsp;</p>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"39\" sizcache=\"17\">\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"39\" sizcache=\"17\">\r\n    <tbody sizset=\"39\" sizcache=\"17\">\r\n        <tr sizset=\"39\" sizcache=\"17\">\r\n            <td sizset=\"39\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/1591/f/1358304897709.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">万事如意 美好祝福</font><font color=\"#333333\">：</font></strong>&ldquo;万事如意&rdquo;是人们对美好生活的一种期盼、祝福。中国自古为礼仪之版，百礼之会，非酒不行，几千年来，美酒琼浆一直是人们表达祝福、庆祝美好生活的最佳裁体。五粮液股份有限公司精心打造的优质产品&ldquo;万事如意&rdquo;系列酒，将人们对美好生活的向往和祝福很好地融入到中国传统的酒文化，以其精良的工艺、完美的包装，成为人们赠送亲友，祝福美好生活的最佳礼品。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p align=\"center\">&nbsp;</p>\r\n</dl>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"40\" sizcache=\"17\">\r\n<p align=\"center\" sizset=\"40\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/1591/g/1358304824493.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizset=\"41\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/1591/g/1358304832447.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizset=\"42\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/1591/g/1358304837892.jpg\" alt=\"\" /></p>\r\n</dl></div>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">品牌文化</h4>\r\n<p sizset=\"43\" sizcache=\"17\">&nbsp;</p>\r\n<table width=\"780\" border=\"0\" cellspacing=\"1\" cellpadding=\"0\" sizset=\"43\" sizcache=\"17\">\r\n    <tbody sizset=\"43\" sizcache=\"17\">\r\n        <tr sizset=\"43\" sizcache=\"17\">\r\n            <td sizset=\"43\" sizcache=\"17\">\r\n            <p sizset=\"43\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/3765/b/1351128317372.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">宜宾，自古以来就是一个多民族杂居的地区。聚居此地的各族人民依托世代承传的习俗和经验，曾经在不同的历史时期，酿制出了各具特色的历史美酒。目前有史可考的，诸如先秦时期僚人酿制的清酒、秦汉时期僰人酿制的蒟酱酒、三国时期鬏鬏苗人用野生小红果酿制的果酒等，都是当时宜宾地区少数民族的杰作，无不闪烁着古代中国人对酿酒技术的独到见解和聪明才智。人持一根竹管直接从罐内吸饮，一边喝一边加水，直到没有酒味为止。</font></p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\">\r\n            <p sizset=\"44\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/3765/b/1351128340762.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">在唐代，戎州官坊用四种粮食酿制了一种&ldquo;春酒&rdquo;。  唐代大诗人杜甫大约在公元743年到了宜宾，当时的戎州刺史杨使君在东楼设宴为他洗尘。杜甫尝到了春酒和宜宾的特产荔枝，即兴咏出&ldquo;重碧拈春酒、轻红擘荔枝&rdquo;的佳句。春酒后来便改名为&ldquo;重碧酒&rdquo;了。  <br />\r\n            在五粮液的酿制工艺成形过程中，最为重要、最具影响的当数&ldquo;姚子雪曲&rdquo;。它是宋代（公元960年－1279年）宜宾绅士姚氏家族私坊酿制，采用玉米、大米、高粱、糯米、荞子五种粮食。  &ldquo;姚子雪曲&rdquo;是五粮液最成熟的雏形。</font></p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n        <tr sizset=\"45\" sizcache=\"17\">\r\n            <td sizset=\"45\" sizcache=\"17\">\r\n            <p sizset=\"45\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/3765/b/1351128376474.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">到了公元1368年的明朝初年，宜宾人陈氏继承了姚氏产业，总结出陈氏秘方，五粮液用的就是&ldquo;陈氏秘方&rdquo;。此酒两名，文人雅士称之为&ldquo;姚子雪曲&rdquo;，下层人民都叫&ldquo;杂粮酒&rdquo;，这就是而今五粮液的直接前身。保留至今的明朝老窖，已有600多年的历史，现仍在使用。<br />\r\n            1909年，陈氏秘方传人邓子均将酒带到一个家宴上。晚清举人杨惠泉品尝了以后说：&ldquo;如此佳酿，名为杂粮酒似嫌凡俗，而姚子雪曲虽雅，但不能体现此酒的韵味。此酒是集五粮之精华而成玉液，更名为&lsquo;五粮液&rsquo;是一个雅俗共赏的名字，而且顾名可思其义。&rdquo;自此五粮液美名问世，悠悠盛名，已将达一个世纪。</font></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">酒厂介绍</h4>\r\n<p sizset=\"46\" sizcache=\"17\">&nbsp;</p>\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"46\" sizcache=\"17\">\r\n    <tbody sizset=\"46\" sizcache=\"17\">\r\n        <tr sizset=\"46\" sizcache=\"17\">\r\n            <td sizset=\"46\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/3765/m/1351129043919.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">五粮液股份有限公司位于金沙江和岷江交汇处、万里长江第一城&mdash;&mdash;&ldquo;酒都&rdquo;宜宾，是以五粮液系列酒生产为主业，塑胶加工、药业、印务、包装、外贸、果酒、伏特加酒矿泉水、运输、饲料、养殖等相配套的、多元化的现代公司制企业集团。集团公司占地5平方公里，现有职工10000余人，主导产品五粮液在中外消费者中享有很高的声誉。前国务院总理李鹏曾亲笔为五粮液挥毫题词：&ldquo;神州琼浆五粮液&rdquo;。</font></p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>&nbsp;</td>\r\n            <td>&nbsp;</td>\r\n            <td>&nbsp;</td>\r\n        </tr>\r\n        <tr sizset=\"47\" sizcache=\"17\">\r\n            <td sizset=\"47\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/3765/m/1351129053724.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td><font color=\"#666666\" size=\"2\">今天的五粮液集团公司，享有&ldquo;花园公司&rdquo;的美名，灿烂远古的酒文化在这里被赋予了新的内涵。精心  规划的厂区、独具匠心的雕塑、现代气魄的建筑、四季常青的花木构筑起极富特色的五粮液企业文化。集团内也有很多有名的景观：全国最高的企业形象雕塑&mdash;&mdash;奋进塔，全国规模最大的酒文化博览馆，气势恢宏的鹏程广场，厂内文化公园酒圣山鹏程广场的瓶楼，高66.8米，底部直径18米，为中国乃至世界最大的瓶形建筑物，瓶楼两旁各衬一小瓶楼，呈品字形状。已经申报为吉尼斯世界最大实物广告。酒圣雕塑，位于酒圣山东面山脚下。为一美少女骑一雄师，待集积万物灵气，而酿成神州琼液。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">获奖荣誉</h4>\r\n<p><font color=\"#666666\" size=\"2\">五粮液酒，自1915年获巴拿马万国博览会金奖，五粮液酒又相继在世界各地的博览会上，共获39次金奖。1995年再获巴拿马第十三届国际贸易博览会金奖，铸就了五粮液&ldquo;八十年金牌不倒&rdquo;的辉煌业绩；2002年6月，在巴拿马&ldquo;第20届国际商展&rdquo;上又一次荣获白酒类唯一金奖，续写了五粮液百年荣誉。  </font></p>\r\n</div>\r\n<p><input id=\"sych\" type=\"hidden\" value=\"商务用酒;婚庆用酒;礼节拜访;聚会用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"41\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"41\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"55\" sizcache=\"27\">\r\n<div class=\"sych1\" id=\"gvsw0\" sizset=\"55\" sizcache=\"27\"><span sizset=\"48\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvsw1.gif\" alt=\"\" /> </span><span sizset=\"49\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvhq1.gif\" alt=\"\" /> </span><span sizset=\"50\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvlj1.gif\" alt=\"\" /> </span><span sizset=\"51\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvjh1.gif\" alt=\"\" /></span></div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>', 'images/201404/thumb_img/427_thumb_G_1398123962473.jpg', 'images/201404/goods_img/427_G_1398123962059.jpg', 'images/201404/source_img/427_G_1398123962824.jpg', '1', '', '1', '1', '1', '1398123962', '100', '0', '0', '0', '0', '0', '0', '1398123962', '0', '', '-1', '-1', '0', '', '0');
