INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('354', '513', 'ECS000354', '53°金奖20汾酒475ml', '+', '51', '171', '', '0', '0.000', '549.60', '458.00', '0.00', '0', '0', '1', '', '', '<p>商品特点</p>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"39\" sizcache=\"17\">\r\n<p sizset=\"39\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/images/upload/Image/untitled(10).jpg\" width=\"750\" height=\"300\" /></p>\r\n<p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">造型独特 清香优雅：</font></strong>二十年陈酿金奖汾酒，包装以黑色为主色调，神秘、冷峻尽现王者气度，其造型采用当年巴拿马获奖瓶型为主体，选取二十年以上陈年优质基酒进行精心勾兑，酒体清香优雅，醇厚绵甜，晶莹透亮，色、香、味、格更具魅力，是一款适合礼品馈赠的佳酿。</font></p>\r\n<p sizset=\"40\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/images/upload/Image/pd_tit_kinds_03.jpg\" width=\"750\" height=\"86\" style=\"border-bottom: medium none; border-left: medium none; padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; border-top: medium none; border-right: medium none; padding-top: 0px\" /></p>\r\n</dl><!--part_box over--><!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">品牌文化</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"750\" sizset=\"41\" sizcache=\"17\">\r\n    <tbody sizset=\"41\" sizcache=\"17\">\r\n        <tr sizset=\"41\" sizcache=\"17\">\r\n            <td sizset=\"41\" sizcache=\"17\">\r\n            <p sizset=\"41\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/4867/b/1351677543908.jpg\" alt=\"\" /></p>\r\n            </td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">从考古发掘文物证实，山西杏花村汾酒酿酒史可上溯到4000多年前的史前期的龙山文化时期。&ldquo;杏花村&rdquo;汾酒早已成为宫廷御酒，应该说：&ldquo;北齐宫廷酒，今日杏花村&rdquo;。这是杏花村汾酒可靠的第一次成名，汾酒史上的第一座丰碑，应申报世界文化遗产。北周文学家庚信亦有&ldquo;三春竹叶青，一曲鹍鸡弦&rdquo;的名句赞誉竹叶青酒。梁简文帝肖纲有：&ldquo;兰羞荐俎，竹酒澄芳&rdquo;的诗句称赞竹叶青酒。</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">唐代与清代更是&ldquo;杏花村&rdquo;汾酒酿酒史上两个鼎盛时期，到唐朝杏花村酿酒作坊达70多家，出现了&ldquo;长街恰副登瀛处，处处街头揭翠帘&rdquo;的盛况。杜甫也到此畅饮，李白前来&ldquo;醉校古碑&rdquo;写下：&ldquo;琼杯绮食青玉案，使我醉饱无归心&rdquo;的诗句。唐代以前的酒是&ldquo;浊酒&rdquo;，&ldquo;杏花村&rdquo;汾酒于唐代在北齐汾清酒的基础上酿出了中国酿酒史上第一家&ldquo;蒸馏白酒&rdquo;成为&ldquo;汾洲贡酒&rdquo;，今天应该说&ldquo;唐时宫廷酒，今日杏花村&rdquo;。到了清代，杏花村的汾酒作坊多达200多家。</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">汾酒发展至今依然是中国酒类行业中一颗璀璨的明星。</font><br />\r\n            &nbsp;</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">酒厂介绍</h4>\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"54\" sizcache=\"27\">\r\n    <tbody sizset=\"54\" sizcache=\"27\">\r\n        <tr sizset=\"54\" sizcache=\"27\">\r\n            <td sizset=\"42\" sizcache=\"17\">\r\n            <p sizset=\"42\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/4867/m/1351677549332.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td sizset=\"54\" sizcache=\"27\">\r\n            <p sizset=\"54\" sizcache=\"27\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\"><span style=\"text-align: left\"><font color=\"#666666\" size=\"2\">山西杏花村汾酒集团有限责任公司为国有独资公司，以生产经营中国名酒&mdash;&mdash;汾酒、竹叶青酒为主营业务，年产名优白酒5万吨，是全国最大的名优白酒生产基地之一。</font></span></p>\r\n            <p style=\"text-align: left; padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\"><font color=\"#666666\" size=\"2\">集团公司下设22个子、分公司，员工8000人，占地面积230万平方米，建筑面积76万平方米。核心企业汾酒厂股份有限公司为公司最大全资子公司，1993年在上海证券交易所挂牌上市，为中国白酒第一股，山西第一股。</font></p>\r\n            </td>\r\n        </tr>\r\n        <tr sizset=\"43\" sizcache=\"17\">\r\n            <td sizset=\"43\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/4867/m/1351677554198.jpg\" alt=\"\" /></td>\r\n            <td>\r\n            <p style=\"text-align: left; padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\"><font color=\"#666666\" size=\"2\">公司拥有&ldquo;杏花村&rdquo;、&ldquo;竹叶青&rdquo;两个中国驰名商标，据2006年《中国500最具价值品牌排行榜》公布，&ldquo;杏花村&rdquo;品牌价值已达47.76亿元。公司主导产品有汾酒、竹叶青酒、玫瑰汾酒、白玉汾酒、以及葡萄酒、啤酒等六大系列。汾酒是我国清香型白酒的典型代表，素以入口绵、落口甜、饮后余香、回味悠长而著称，在国内外享有较高的知名度、美誉度和忠诚度。主要品种有国藏汾酒、青花瓷汾酒、中华汾酒、老白汾酒等。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: 宋体, Verdana, Arial; color: rgb(60,62,61); font-size: 14px; padding-top: 0px\">&nbsp;</p>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">获奖荣誉</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"750\" sizset=\"44\" sizcache=\"17\">\r\n    <tbody sizset=\"44\" sizcache=\"17\">\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\"><img src=\"http://img01.jiuxi