INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('449', '883', 'ECS000449', '50°古井经典老窖500ml', '+', '18', '200', '', '0', '0.000', '70.80', '59.00', '0.00', '0', '0', '1', '', '', '<p>商品特点</p>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"39\" sizcache=\"17\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"39\" sizcache=\"17\">\r\n    <tbody sizset=\"39\" sizcache=\"17\">\r\n        <tr sizset=\"39\" sizcache=\"17\">\r\n            <td sizset=\"39\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6988/f/1359605330827.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#333333\" size=\"2\"><strong>好酒不贵 古井老窖</strong></font></p>\r\n            <p><font color=\"#666666\" size=\"2\">古井经典老窖经典的包装，沉稳的色调，使得古井老窖更加有成熟稳重的魅力，他沿用古老的酿造方式，选用有天下名井之称的古井之水，因此，古井经典老窖色清如水晶，香纯似幽兰、入口甘美醇和、回味经久不息，低端的价格也是古井经典老窖被消费者青睐的原因之一，好酒不贵这才是此酒的真谛！</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</dl><!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"40\" sizcache=\"17\">\r\n<p align=\"center\" sizset=\"40\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6988/g/1359605340246.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizset=\"41\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6988/g/1359605346333.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizset=\"42\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6988/g/1359605351983.jpg\" alt=\"\" /></p>\r\n</dl><!--detail_prtpic over--></div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">品牌文化</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"43\" sizcache=\"17\">\r\n    <tbody sizset=\"43\" sizcache=\"17\">\r\n        <tr sizset=\"43\" sizcache=\"17\">\r\n            <td sizset=\"43\" sizcache=\"17\">\r\n            <p sizset=\"43\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/b/1359620295949.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td><font color=\"#666666\" size=\"2\">古井贡酒有着悠久的历史。南北朝时，在亳州的减店集，人们发现有一口古井，井水清洌甜美，人们用此井水酿酒、泡茶，回味无穷。相传，有个将军因作战失利，临死前将所用的兵器投入井里。谁知此后井水比先前更清淳透明，爽口润喉，所酿之酒，十里飘香，古井名声大噪，人们称之为&ldquo;天下名井&rdquo;。据史书记载，古井贡酒的前身是&ldquo;九酝春酒&rdquo;，为曹操令手下人酿造。后曹操将&ldquo;九酝春酒&rdquo;及酿酒方法&ldquo;九酝酒法&rdquo;献给汉献帝刘协，献帝大加赞赏，作为宫廷用酒。从此，亳州一带酿酒作坊如雨后春笋发展起来。</font></td>\r\n        </tr>\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/m/1359620316350.jpg\" alt=\"\" /></td>\r\n            <td><font color=\"#666666\" size=\"2\">到了宋代，减店集已成了有名的产酒地，当地百姓至今还有&ldquo;涡水鳜鱼苏水鲤，胡芹减酒宴贵宾&rdquo;的说法。明代万历年间，阁老沈鲤在万历帝的庆典上，把&ldquo;减酒&rdquo;当作家乡酒进贡朝廷，万历帝饮后连连叫好，钦定此酒为贡品，命其年年进贡，&ldquo;贡酒&rdquo;之名由此而得。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">酒厂介绍</h4>\r\n<p sizset=\"45\" sizcache=\"17\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"45\" sizcache=\"17\">\r\n    <tbody sizset=\"45\" sizcache=\"17\">\r\n        <tr sizset=\"45\" sizcache=\"17\">\r\n            <td sizset=\"45\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/m/1359620312675.jpg\" alt=\"\" /></td>\r\n            <td><font color=\"#666666\" size=\"2\">古井集团是中国老八大名酒企业，是中国第一家同时发行A、B两支股票的白酒类上市公司安徽古井贡酒股份有限公司的母公司，坐落在历史名人曹操与华佗故里――安徽省亳州市。公司的前身为起源于明代正德十年（公元1515年）的公兴槽坊，1959年转制为省营亳县古井酒厂。目前公司拥有员工8000多名，集酒业、酒店业、农产品深加工、房地产业等为一体，是国家大型一档企业。在2010年第二届中国酒类品牌价值评议活动中，古井贡以81.72亿元喜摘&ldquo;中华白酒十大全球代表性品牌&rdquo;，同时古井集团被授予&ldquo;中国白酒申请国家暨世界非物质文化遗产发起人&rdquo;。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">获奖荣誉</h4>\r\n<p><font color=\"#666666\" size=\"2\">1963年古井贡酒在第二届全国评酒会上首次被评为八大名酒，名列第二。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1979、1984年，在第三、四届全国评酒会上，古井贡酒再次被评为国家名酒。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1988年l0月，在巴黎举办的第十三届国际食品博览会上，古井贡酒获金夏尔奖，是本届唯&mdash;获奖的中国名酒。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1989年12月，古井首次进入按利税排序的中国500家最大工业企业行列，成为共和国经济大厦的柱石。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1989年在第五届全国评酒会上，古井贡酒实现金牌&ldquo;四联冠&rdquo;。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1991年5月，古井牌古井特曲被国家轻工部评为&ldquo;全国轻工业优质产品&rdquo;。 </font></p>\r\n</div>\r\n<p><!--part_box--><!--part_box--><!-- 酒具适用场合 start--><!-- 酒具适用场合 end--><!--part_box--><input id=\"sych\" type=\"hidden\" value=\"礼节拜访;聚会用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"41\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"41\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"54\" sizcache=\"27\">\r\n<div id=\"gvsw0\" class=\"sych1\" sizset=\"54\" sizcache=\"27\"><span sizset=\"46\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvsw0.gif\" alt=\"\" /> </span><span sizset=\"47\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvhq0.gif\" alt=\"\" /> </span><span sizset=\"48\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvlj1.gif\" alt=\"\" /> </span><span sizset=\"49\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvjh1.gif\" alt=\"\" /> </span></div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>', 'images/201404/thumb_img/449_thumb_G_1398125471800.jpg', 'images/201404/goods_img/449_G_1398125471776.jpg', 'images/201404/source_img/449_G_1398125471435.jpg', '1', '', '1', '1', '0', '1398125471', '100', '0', '0', '0', '0', '0', '0', '1412534077', '0', '', '-1', '-1', '0', '', '0');
INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('450', '717', 'ECS000450', '46°洋河蓝色经典海之蓝480ml', '+', '20', '192', '', '0', '0.000', '258.00', '139.00', '0.00', '0', '0', '1', '', '', '<h4 class=\"h4_ass\" sizset=\"36\" sizcache=\"39\">商品特点</h4>\r\n<p>&nbsp;</p>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"53\" sizcache=\"27\">\r\n<p sizset=\"53\" sizcache=\"27\"><span style=\"color: rgb(255, 0, 0);\"><strong>温馨提示：此款酒因批次不同，酒质相同，外包装略有差异，产品随机发货，请以收到的实物为准，给您带来的不便，敬请谅解！</strong></span></p>\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"39\" sizcache=\"17\">\r\n    <tbody sizset=\"39\" sizcache=\"17\">\r\n        <tr sizset=\"39\" sizcache=\"17\">\r\n            <td sizset=\"39\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/312/f/1384066434737.jpg\" alt=\"\" /></td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">●  胸怀天下，海纳百川：</font></strong>大海，容纳百川，托举红日，每一朵浪花都激荡着不断创造的力量。在金色的航道上寄托蓝色的畅享，在蓝色的乐章里谱写金色的诗行，海之蓝，将大海的韵味融于一瓶，一水之间，尽显您胸襟的辽阔深远。</font></p>\r\n            <p><font color=\"#666666\" size=\"2\"><strong><font color=\"#333333\">●  高端设计，尽显创意：</font></strong>海之蓝打破传统包装技术，曲线形瓶盖像是绽放的花朵，瓶底采用了扇形曲线，宛如窈窕淑女的裙摆婀娜多姿，立体璀璨的商标，金色与蓝色的搭配，张扬着洋河品牌的高端形象。 </font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p sizset=\"40\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/images/upload/Image/pd_tit_kinds_02.jpg\" style=\"color: rgb(0, 0, 0); line-height: normal; font-family: Arial, Verdana, sans-serif; font-size: 12px;\" /></p>\r\n<p>&nbsp;</p>\r\n</dl>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"41\" sizcache=\"17\">\r\n<p align=\"center\" sizset=\"41\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/312/g/1384066485623.jpg\" alt=\"\" /><img src=\"http://img10.jiuxian.com/bill/upload/goods/312/g/1384066492260.jpg\" alt=\"\" /><br />\r\n<img src=\"http://img10.jiuxian.com/2014/0218/116385654029496ebc430740fbc48e8a.jpg\" alt=\"\" /></p>\r\n</dl></div>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">品牌文化</h4>\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"44\" sizcache=\"17\">\r\n    <tbody sizset=\"44\" sizcache=\"17\">\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/4390/b/1351838154484.jpg\" alt=\"\" /></td>\r\n            <td><font color=\"#666666\" size=\"2\">据传，洋河大曲在唐代就已享盛名，尚可考证的历史已有四百多年，明末清初已闻名遐迩。当时曾有九个省的客商在此设立会馆，省内外七十多位商人客籍于此，竞酿美酒，使洋河镇的酿酒业更加兴隆繁盛。据《泗阳县志》记载，明朝著名诗人邹辑在《咏白洋河》中写到：&ldquo;白洋河下春水碧，白洋河中多沽客，春风二月柳条新，却念行人千里隔，行客年年任往来，居人自在洋河曲&rdquo;。清雍正年间，洋河大曲已行销江淮一带，颇受欢迎，有&ldquo;福泉酒海清香美，味占江淮第一家&rdquo;之誉，并被列为清皇室贡品。据记载，清乾隆皇帝第二次南巡时，在宿迁建有行宫，留住七天，品尝洋河大曲后挥毫留下了&ldquo;酒味香醇，真佳酒也&rdquo;的赞语。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">酒厂介绍</h4>\r\n<p>&nbsp;</p>\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"45\" sizcache=\"17\">\r\n    <tbody sizset=\"45\" sizcache=\"17\">\r\n        <tr sizset=\"45\" sizcache=\"17\">\r\n            <td sizset=\"45\" sizcache=\"17\">\r\n            <p sizset=\"45\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/4390/m/1351837996758.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">江苏洋河酒厂股份有限公司位于苏北古镇&mdash;&mdash;洋河，面临徐淮公路，背靠京杭运河，交通畅达，酒业兴旺。洋河大曲以产地而得名，系以优质高粱为原料，以小麦、大麦、豌豆制成的高温火曲为发酵剂，辅以闻名遐迩的美人泉水精工酿制而成。</font></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n        <tr sizset=\"46\" sizcache=\"17\">\r\n            <td sizset=\"46\" sizcache=\"17\">\r\n            <p sizset=\"46\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/4390/m/1351838160770.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">企业在全国同行率先通过包括ISO9001质量认证、ISO14001环境管理体系认证、OHSAS18001职业健康安全管理体系和ISO22000食品安全管理体系认证，较早成立省级企业技术中心，被国家、省授予博士后技术创新中心称号。正是以雄厚的技术创新优势和质量管理优势，洋河酒厂参与起草了浓香型白酒国家新标准，江苏省生物酿酒技术研究院也落户洋河。特别是2003年以来，洋河酒厂成功打造&ldquo;绵柔型&rdquo;白酒经典之作洋河蓝色经典，在白酒市场掀起&ldquo;蓝色风暴&rdquo;，并成为备受行业关注的成功营销范例，企业销售连续多年保持50%以上增幅。 </font></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n        <tr sizset=\"47\" sizcache=\"17\">\r\n            <td sizset=\"47\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/4390/m/1351838242895.jpg\" alt=\"\" /></td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">近年来，洋河全力打造&ldquo;绵柔型&rdquo;白酒经典之作&mdash;&mdash;洋河蓝色经典。世界上最宽广的是海，比海更高远的是天空，比天空更博大的是男人的情怀。蓝色是开放的象征，是时尚的标志，是现代的感觉，是品位的表现；天之高为蓝，海之深为蓝，梦之遥为蓝，这是对洋河蓝色文化的一种演绎，体现了人们对宽广、博大胸怀的追求。</font></p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p><input id=\"sych\" type=\"hidden\" value=\"商务用酒;礼节拜访;聚会用酒;婚庆用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"54\" sizcache=\"27\">\r\n<div class=\"sych1\" id=\"gvsw0\" sizset=\"54\" sizcache=\"27\"><span sizset=\"48\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvsw1.gif\" alt=\"\" /> </span><span sizset=\"49\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvhq1.gif\" alt=\"\" /> </span><span sizset=\"50\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvlj1.gif\" alt=\"\" /> </span><span sizset=\"51\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvjh1.gif\" alt=\"\" /></span></div>\r\n</div>\r\n</div>\r\n<p>&nbsp;</p>', 'images/201404/thumb_img/450_thumb_G_1398125495624.jpg', 'images/201404/goods_img/450_G_1398125495555.jpg', 'images/201404/source_img/450_G_1398125495274.jpg', '1', '', '1', '1', '1', '1398125495', '100', '0', '0', '0', '0', '0', '0', '1412534075', '0', '', '-1', '-1', '0', '', '0');
INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('451', '883', 'ECS000451', '42°古井淡雅蓝花450ml', '+', '21', '200', '', '0', '0.000', '70.80', '59.00', '0.00', '0', '0', '1', '', '', '<h4 class=\"h4_ass\" sizset=\"36\" sizcache=\"39\">商品特点</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"39\" sizcache=\"17\">\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"39\" sizcache=\"17\">\r\n    <tbody sizset=\"39\" sizcache=\"17\">\r\n        <tr sizset=\"39\" sizcache=\"17\">\r\n            <td sizset=\"39\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/f/1359617143136.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#333333\" size=\"2\"><strong>淡雅古井 极佳品质</strong></font></p>\r\n            <p><font color=\"#666666\" size=\"2\">古井淡雅蓝花全身以淡雅的蓝色花纹为主，优雅的白色瓷瓶上点缀的蓝色花纹彰显淡雅本色，更像似一婀娜多姿的闺中少女，优雅、端庄、沉静，古井淡雅蓝花的酒质如同包装一样有着清新淡雅、入口绵甜、回味悠长等风格。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</dl><!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"40\" sizcache=\"17\">\r\n<p align=\"center\" sizset=\"40\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/g/1359617154791.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizset=\"41\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/g/1359617160768.jpg\" alt=\"\" /></p>\r\n<p align=\"center\">&nbsp;</p>\r\n<p align=\"center\" sizset=\"42\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/g/1359617165275.jpg\" alt=\"\" /></p>\r\n</dl><!--detail_prtpic over--></div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">品牌文化</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"43\" sizcache=\"17\">\r\n    <tbody sizset=\"43\" sizcache=\"17\">\r\n        <tr sizset=\"43\" sizcache=\"17\">\r\n            <td sizset=\"43\" sizcache=\"17\">\r\n            <p sizset=\"43\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/b/1359620295949.jpg\" alt=\"\" /></p>\r\n            <p>&nbsp;</p>\r\n            </td>\r\n            <td><font color=\"#666666\" size=\"2\">古井贡酒有着悠久的历史。南北朝时，在亳州的减店集，人们发现有一口古井，井水清洌甜美，人们用此井水酿酒、泡茶，回味无穷。相传，有个将军因作战失利，临死前将所用的兵器投入井里。谁知此后井水比先前更清淳透明，爽口润喉，所酿之酒，十里飘香，古井名声大噪，人们称之为&ldquo;天下名井&rdquo;。据史书记载，古井贡酒的前身是&ldquo;九酝春酒&rdquo;，为曹操令手下人酿造。后曹操将&ldquo;九酝春酒&rdquo;及酿酒方法&ldquo;九酝酒法&rdquo;献给汉献帝刘协，献帝大加赞赏，作为宫廷用酒。从此，亳州一带酿酒作坊如雨后春笋发展起来。</font></td>\r\n        </tr>\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/m/1359620316350.jpg\" alt=\"\" /></td>\r\n            <td><font color=\"#666666\" size=\"2\">到了宋代，减店集已成了有名的产酒地，当地百姓至今还有&ldquo;涡水鳜鱼苏水鲤，胡芹减酒宴贵宾&rdquo;的说法。明代万历年间，阁老沈鲤在万历帝的庆典上，把&ldquo;减酒&rdquo;当作家乡酒进贡朝廷，万历帝饮后连连叫好，钦定此酒为贡品，命其年年进贡，&ldquo;贡酒&rdquo;之名由此而得。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">酒厂介绍</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"45\" sizcache=\"17\">\r\n    <tbody sizset=\"45\" sizcache=\"17\">\r\n        <tr sizset=\"45\" sizcache=\"17\">\r\n            <td sizset=\"45\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/6989/m/1359620312675.jpg\" alt=\"\" /></td>\r\n            <td><font color=\"#666666\" size=\"2\">古井集团是中国老八大名酒企业，是中国第一家同时发行A、B两支股票的白酒类上市公司安徽古井贡酒股份有限公司的母公司，坐落在历史名人曹操与华佗故里――安徽省亳州市。公司的前身为起源于明代正德十年（公元1515年）的公兴槽坊，1959年转制为省营亳县古井酒厂。目前公司拥有员工8000多名，集酒业、酒店业、农产品深加工、房地产业等为一体，是国家大型一档企业。在2010年第二届中国酒类品牌价值评议活动中，古井贡以81.72亿元喜摘&ldquo;中华白酒十大全球代表性品牌&rdquo;，同时古井集团被授予&ldquo;中国白酒申请国家暨世界非物质文化遗产发起人&rdquo;。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">获奖荣誉</h4>\r\n<p><font color=\"#666666\" size=\"2\">1963年古井贡酒在第二届全国评酒会上首次被评为八大名酒，名列第二。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1979、1984年，在第三、四届全国评酒会上，古井贡酒再次被评为国家名酒。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1988年l0月，在巴黎举办的第十三届国际食品博览会上，古井贡酒获金夏尔奖，是本届唯&mdash;获奖的中国名酒。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1989年12月，古井首次进入按利税排序的中国500家最大工业企业行列，成为共和国经济大厦的柱石。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1989年在第五届全国评酒会上，古井贡酒实现金牌&ldquo;四联冠&rdquo;。 </font></p>\r\n<p><font color=\"#666666\" size=\"2\">1991年5月，古井牌古井特曲被国家轻工部评为&ldquo;全国轻工业优质产品&rdquo;。 </font></p>\r\n</div>\r\n<p><!--part_box--><!--part_box--><!-- 酒具适用场合 start--><!-- 酒具适用场合 end--><!--part_box--><input id=\"sych\" type=\"hidden\" value=\"礼节拜访;聚会用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"41\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"41\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"54\" sizcache=\"27\">\r\n<div id=\"gvsw0\" class=\"sych1\" sizset=\"54\" sizcache=\"27\"><span sizset=\"46\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvsw0.gif\" alt=\"\" /> </span><span sizset=\"47\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvhq0.gif\" alt=\"\" /> </span><span sizset=\"48\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvlj1.gif\" alt=\"\" /> </span><span sizset=\"49\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvjh1.gif\" alt=\"\" /> </span></div>\r\n</div>\r\n</div>', 'images/201404/thumb_img/451_thumb_G_1398125555720.jpg', 'images/201404/goods_img/451_G_1398125555089.jpg', 'images/201404/source_img/451_G_1398125555541.jpg', '1', '', '1', '1', '0', '1398125555', '100', '0', '0', '0', '0', '0', '0', '1412534073', '0', '', '-1', '-1', '0', '', '0');
