INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('465', '876', 'ECS000465', '38°柔雅西凤酒500ml', '+', '17', '193', '', '0', '0.000', '75.00', '32.00', '0.00', '0', '0', '1', '', '', '<h4 class=\"h4_ass\" sizset=\"36\" sizcache=\"39\">商品特点</h4>\r\n<p>&nbsp;</p>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"39\" sizcache=\"17\">\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"39\" sizcache=\"17\">\r\n    <tbody sizset=\"39\" sizcache=\"17\">\r\n        <tr sizset=\"39\" sizcache=\"17\">\r\n            <td sizset=\"39\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/12107/f/1386237011258.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td><font size=\"2\"><strong><font color=\"#333333\">醇香典雅 甘润挺爽<br />\r\n            </font></strong><font color=\"#666666\">西凤酒，是中国凤香型白酒的鼻祖代表。此款酒是为数不多的一款浓香型白酒，却是&ldquo;清而不淡，浓而不艳，集清香、浓香特长于一体，酸、甜、苦、辣、香五味协调俱全，均不出头&rdquo;的独特风格特点。</font></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</dl>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"40\" sizcache=\"17\">\r\n<p align=\"center\" sizset=\"40\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/12107/g/1386237027575.jpg\" alt=\"\" /><img src=\"http://img01.jiuxian.com/upload/goods/12107/g/1386237034626.jpg\" alt=\"\" /><img src=\"http://img01.jiuxian.com/upload/goods/12107/g/1386237040199.jpg\" alt=\"\" /></p>\r\n</dl></div>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">品牌文化</h4>\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"43\" sizcache=\"17\">\r\n    <tbody sizset=\"43\" sizcache=\"17\">\r\n        <tr sizset=\"43\" sizcache=\"17\">\r\n            <td sizset=\"43\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/12786/m/1385810301693.jpg\" alt=\"\" /></td>\r\n            <td><font color=\"#666666\" size=\"2\"> </font></td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">西凤酒古称&ldquo;秦酒&rdquo;、&ldquo;柳林酒&rdquo;。为中国四大名酒之一，产于陕西省宝鸡市凤翔县柳林镇，始于殷商</font><font color=\"#666666\" size=\"2\">，盛于唐宋，已有三千多年的历史，有苏轼咏酒等诸多典故，文化灿烂。2012年第四届&ldquo;华樽杯&rdquo;中国</font><font color=\"#666666\" size=\"2\">酒类品牌价值排行榜中以213.26亿的品牌价值位居中国白酒类品牌价值排行榜第6位。并荣获2012年度华</font><font color=\"#666666\" size=\"2\">樽杯&ldquo;中国酒类十大最具全球竞争力品牌&rdquo;、&ldquo;中国十大最畅销白酒品牌&rdquo;、&ldquo;中国酒类十大品牌组合</font><font color=\"#666666\" size=\"2\">&rdquo;及&ldquo;富国强军十大突出贡献品牌&rdquo;。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p>&nbsp;</p>\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">酒厂介绍</h4>\r\n<table width=\"780\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" sizset=\"44\" sizcache=\"17\">\r\n    <tbody sizset=\"44\" sizcache=\"17\">\r\n        <tr sizset=\"44\" sizcache=\"17\">\r\n            <td sizset=\"44\" sizcache=\"17\"><img src=\"http://img01.jiuxian.com/upload/goods/12786/b/1385809891245.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td><font color=\"#666666\" size=\"2\">陕西西凤酒集团股份有限公司是1999年10月以陕西省西凤酒厂生产经营性净资产为核心，联合其他企业法人和社会法人组建的公司制企业。陕西省西凤酒厂是1956年在周恩来总理的亲切关怀下创建。2010年，股份公司进行了改制重组，股东由原来的4个增加到目前26个，实现了股权多元化。目前，股份公司总资产26亿元，占地面积86.8万平方米，员工近3000人，其中，国家级白酒评委5人,省级白酒评委15人,高级酿酒师26人，年产能约5万吨，属国家大型一档企业，是西北地区规模最大的国家名酒制造商，陕西省利税大户之一。</font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<p><input id=\"sych\" type=\"hidden\" value=\"商务用酒;婚庆用酒;礼节拜访;聚会用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"56\" sizcache=\"27\">\r\n<div class=\"sych1\" id=\"gvsw0\" sizset=\"56\" sizcache=\"27\"><span sizset=\"45\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvsw1.gif\" alt=\"\" /> </span><span sizset=\"46\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvhq1.gif\" alt=\"\" /> </span><span sizset=\"47\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvlj1.gif\" alt=\"\" /> </span><span sizset=\"48\" sizcache=\"17\"><img src=\"http://misc.jiuxian.com/img/goods/gvjh1.gif\" alt=\"\" /></span></div>\r\n</div>\r\n</div>', 'images/201404/thumb_img/465_thumb_G_1398126664326.jpg', 'images/201404/goods_img/465_G_1398126664333.jpg', 'images/201404/source_img/465_G_1398126664809.jpg', '1', '', '1', '1', '0', '1398126664', '100', '0', '0', '0', '0', '0', '0', '1412534030', '0', '', '-1', '-1', '0', '', '0');
