INSERT INTO `jindong_goods` ( `goods_id`, `cat_id`, `goods_sn`, `goods_name`, `goods_name_style`, `click_count`, `brand_id`, `provider_name`, `goods_number`, `goods_weight`, `market_price`, `shop_price`, `promote_price`, `promote_start_date`, `promote_end_date`, `warn_number`, `keywords`, `goods_brief`, `goods_desc`, `goods_thumb`, `goods_img`, `original_img`, `is_real`, `extension_code`, `is_on_sale`, `is_alone_sale`, `integral`, `add_time`, `sort_order`, `is_delete`, `is_best`, `is_new`, `is_hot`, `is_promote`, `bonus_type_id`, `last_update`, `goods_type`, `seller_note`, `give_integral`, `rank_integral`, `suppliers_id`, `is_check`, `is_shipping` ) VALUES  ('493', '885', 'ECS000493', '52°宝丰国色清香鉴品500ml（双瓶套装）', '+', '56', '202', '', '0', '0.000', '403.20', '336.00', '0.00', '0', '0', '1', '', '', '<p>商品特点</p>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"36\" sizcache=\"17\">\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"36\" sizcache=\"17\">\r\n    <tbody sizset=\"36\" sizcache=\"17\">\r\n        <tr sizset=\"36\" sizcache=\"17\">\r\n            <td sizset=\"36\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/3745/f/1366252576757.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#333333\" size=\"2\"><strong>清香淡雅 优质佳酿</strong></font></p>\r\n            <p><font color=\"#666666\" size=\"2\">国色清香鉴品酒，淡雅的蓝色半透明包装，如同其酒质一般清新淡雅，国色清香精选原生态产区优质原粮，吮天地之灵气，吸日月之精华，好粮好曲好工艺，假以酿制时日，方成佳酿。以贮藏15年的优质原酒为酒基，科学调制而成。酒体清澈透明、柔和饱满，闻之清香芬芳，品之甘润爽口。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p sizset=\"37\" sizcache=\"17\"><img alt=\"\" src=\"http://img01.jiuxian.com/images/upload/Image/pd_tit_kinds_03.jpg\" width=\"750\" height=\"86\" style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\" /></p>\r\n</dl><!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"36\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"36\" sizcache=\"39\">商品展示</h4>\r\n<dl class=\"ass_share_dl2 clearfix\" sizset=\"38\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/14070/g/1389663314366.jpg\" alt=\"\" /> </dl><!--detail_prtpic over--></div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"37\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"37\" sizcache=\"39\">品牌文化</h4>\r\n<p>&nbsp;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"39\" sizcache=\"17\">\r\n    <tbody sizset=\"39\" sizcache=\"17\">\r\n        <tr sizset=\"39\" sizcache=\"17\">\r\n            <td sizset=\"39\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/3760/b/1352256713810.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">宝丰酒，以四千余年&ldquo;仪狄造酒&rdquo;古法为传承，不断发展创新，并坚持&ldquo;四清&rdquo;酿造标准，缔造&ldquo;清香纯正、绵甜柔和、甘润爽口、回味悠长&rdquo;等特点品质，连续摘夺我国白酒评比会大奖，成为清香型白酒的典型代表。先后出口新加坡、几内亚，直销中国香港、澳门、台湾等地区，原酒出口量全国第一，成品酒出口河南省第一。 </font></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>&nbsp;</td>\r\n            <td>&nbsp;</td>\r\n            <td>&nbsp;</td>\r\n        </tr>\r\n        <tr sizset=\"40\" sizcache=\"17\">\r\n            <td sizset=\"40\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/3760/b/1352256827107.jpg\" alt=\"\" /></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">历史，为企业打上深厚烙印。从夏、禹时的仪狄造酒起源；到隋唐时期的兴旺鼎盛，及明清时候的一路流香，在四千多年的历史中，宝丰酿造技艺绵延不绝，从未间断。历史的发展，也造就宝丰清香纯正的内涵。从大唐王朝把宝丰酒定为&ldquo;贡酒&rdquo;；到北宋，官方专设&ldquo;酒务&rdquo;，以管辖、指导宝丰酿造，宝丰清香，一路流芳。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"38\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"38\" sizcache=\"39\">酒厂介绍</h4>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"780\" sizset=\"41\" sizcache=\"17\">\r\n    <tbody sizset=\"41\" sizcache=\"17\">\r\n        <tr sizset=\"41\" sizcache=\"17\">\r\n            <td sizset=\"41\" sizcache=\"17\"><font color=\"#666666\" size=\"2\" sizset=\"41\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/3760/m/1363331921211.jpg\" alt=\"\" /></font></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><br />\r\n            <font color=\"#666666\" size=\"2\">始建于1948年的宝丰酒业有限公司，是苏鲁豫皖四省中唯一一家生产</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">清香型白酒的企业，位列中国十七大名酒；所生产酿造的清香产品系</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">列，是河南名牌产品。企业先后被评为全国质量管理优秀企业、全国</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">食品工业优秀企业、全国酿酒行业双百家企业、全国白酒工业百强企</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">业、ISO9001质量体系认证企业等。2006年4月，宝丰酒业成功改制，</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">迎来企业全新发展。为是实现可持续发展，近年来宝丰投资3亿多元</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">，进行技术改造和科研开发。</font></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>&nbsp;</td>\r\n            <td>&nbsp;</td>\r\n            <td>&nbsp;</td>\r\n        </tr>\r\n        <tr sizset=\"42\" sizcache=\"17\">\r\n            <td sizset=\"42\" sizcache=\"17\"><font color=\"#666666\" size=\"2\" sizset=\"42\" sizcache=\"17\"><img src=\"http://img10.jiuxian.com/bill/upload/goods/3760/m/1363331928176.jpg\" alt=\"\" /></font></td>\r\n            <td>&nbsp;</td>\r\n            <td>\r\n            <p><font color=\"#666666\" size=\"2\">2008年，投资300多万元，对制曲设备、酿造设备进行更新改造，提</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">高了产品质量和产量；投资6800多万元，上了万吨储酒工程暨配套自</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">动化灌装生产线，建成后新增1万吨储酒能力，5万吨调酒及灌装能力</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">；加大生产，使企业年销量达20000吨，成品酒产品30000吨，原酒存</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">酒量30000吨；2010年完成产值8.9亿元&hellip;&hellip;此外，宝丰还组织科研人</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">员自行研发出哈工业首创的地缸发酵温度控制系统，成功解决发酵温</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">度控制难的问题，提高了酿酒产量和质量，由此，宝丰逐步成为了集</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">研发、生产、酿造、灌注、包装、销售为一体的大型酒企，各项主要</font></p>\r\n            <p><font color=\"#666666\" size=\"2\">经济指标均大幅攀升，销售收入连年翻番。</font></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n<!--part_box over-->\r\n<div class=\"part_box clearfix\" sizset=\"39\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"39\" sizcache=\"39\">获奖荣誉</h4>\r\n<p><font color=\"#666666\" size=\"2\">&middot;1956年，宝丰酒被评为河南名酒；</font></p>\r\n<p><font color=\"#666666\" size=\"2\">&middot;1979年、1984年，在全国第三届、第四届白酒评比中，蝉联两届国优，并获国家银质奖；</font></p>\r\n<p><font color=\"#666666\" size=\"2\">&middot;1989年，全国第五届白酒评比中，以得分最高荣获国家金质奖，晋升17大中国名酒；</font></p>\r\n<p><font color=\"#666666\" size=\"2\">&middot;2002年，39度、46度清香型宝丰酒，被授予&ldquo;中华人民共和国国家标准样品&rdquo;称号；</font></p>\r\n<p><font color=\"#666666\" size=\"2\">&middot;2002年，宝丰系列酒获国家&ldquo;原产地&rdquo;标记保护注册认证；</font></p>\r\n<p><font color=\"#666666\" size=\"2\">&middot;2008年，宝丰酒酿造技艺入选第二批&ldquo;国家级非物质文化遗产&rdquo;名录；</font></p>\r\n<p><font color=\"#666666\" size=\"2\">&middot;2009年，宝丰酒获&ldquo;纯粮固态发酵白酒标志&rdquo;授牌，荣升高档之列，体现酿造最高荣誉；</font></p>\r\n<p><font color=\"#666666\" size=\"2\">&middot;2010年，荣获&ldquo;中华老字号&rdquo;（4000余年清香典范）荣耀认证；</font></p>\r\n</div>\r\n<p><!--part_box--><!--part_box--><!-- 酒具适用场合 start--><!-- 酒具适用场合 end--><!--part_box--><input id=\"sych\" type=\"hidden\" value=\"礼节拜访;聚会用酒\" /></p>\r\n<div class=\"part_box clearfix\" sizset=\"40\" sizcache=\"39\">\r\n<h4 class=\"h4_ass\" sizset=\"40\" sizcache=\"39\">适宜场合</h4>\r\n<div class=\"sych\" sizset=\"56\" sizcache=\"27\">\r\n<div id=\"gvsw0\" class=\"sych1\" sizset=\"56\" sizcache=\"27\"><span sizset=\"43\" sizcache=\